import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Function to format a date as 3/20/2024 - THURSDAY
const formatDate = (date) => {
    const dayOfWeek = new Intl.DateTimeFormat("en-US", { weekday: "long" }).format(date).toUpperCase();
    const formattedDate = `${date.getMonth() + 1}/${date.getDate()}/${date.getFullYear()} - ${dayOfWeek}`;
    return formattedDate;
};

function ActivityForm() {
    const [formData, setFormData] = useState({ date: '', activity: '', tags: [], morning: [], wake: '' });
    const [message, setMessage] = useState('');
    const [dateMessage, setDateMessage] = useState('');
    const [logEntry, setLogEntry] = useState('');

    useEffect(() => {
        setDateMessage('Watch here for hints.');
    }, []);

    const handleChange = (e) => {
        const { name, value, options } = e.target;
        if ((name === 'tags') && options) {
            const selectedTags = Array.from(options).filter(option => option.selected).map(option => option.value);
            setFormData({ ...formData, [name]: selectedTags });
        } else {
            setFormData({ ...formData, [name]: value });
            if (name === 'date') {
                checkDay(value);
            }
        }
    };

    const checkDay = (date) => {
        const selectedDate = new Date(date + 'T00:00:00');
        if (selectedDate.getDay() === 0) {
            setDateMessage('This is a Sunday. Add a "Meal Planning and Grocery Shopping" and a "Shower" line item for today.');
        } else if (selectedDate.getDay() === 6) {
            setDateMessage('This is a Saturday. Add an "Outing" line item for today.');
        } else {
            setDateMessage('Watch here for hints.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5001/save-log', formData);
            setMessage(response.data.message);

            const formattedDate = formatDate(new Date(formData.date + 'T00:00:00'));
            const logEntry = `\r\n\r\n${formData.tags.length > 0 ? `NOTE FOR HEATHER: ${formData.tags.join('. ')}. \r\n` : ''}${formattedDate}\r\n${formData.wake} ${formData.morning} ${formData.breakfast} ${formData.midday} ${formData.lunch} ${formData.afternoon} ${formData.dinner} ${formData.evening} ${formData.bed} `;
            setLogEntry(logEntry);
        } catch (error) {
            if (error.response) {
                setMessage(error.response.data.message || 'Error saving activity log');
            } else {
                setMessage('Error saving activity log');
            }
            console.error('Error saving activity log:', error);
        }
    };

    const handleDragStart = (e, text) => {
        e.dataTransfer.setData('text/plain', text);
    };

    const handleDrop = (e, name) => {
        e.preventDefault();
        const text = e.dataTransfer.getData('text/plain');
        setFormData({ ...formData, [name]: formData[name] + text });
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    return (
        <div>
            <h1>Alex Activity Log</h1>

            <div className={"form-data-hint-general hint-block-bottom-border"}>
                <p className={"bold"}>Requirements</p>
                <ul>
                    <li>CHORE at least 1 time a week.</li>
                    <li>CAD at least 1 time a week.</li>
                    <li>OUTING every Saturday.</li>
                    <li>MEAL PLAN and GROCERY SHOP every Sunday (or other day as needed).</li>
                    <li>NEW SKILL at least 1 time a month.</li>
                    <li>BEHAVIOR ISSUE at least 1 time a month.</li>

                    <li>Add "Sponsor and Relief conducted a Fire Drill and helped Alex leave the house when the alarm
                        went off and helped Alex walk to the meeting point in the front yard." at least 1 time a month.
                    </li>
                    <li>Add "Sponsor/Relief helped Alex shower and with her ADLs" every Sunday.</li>
                    <li>Add "Sponsor/Relief helped Alex shower and with her ADLs" at least 2 more times a week.</li>
                </ul>
            </div>

            <form onSubmit={handleSubmit}>
                <div className={"form-data-textarea-block form-data-container-tags"}>
                    <label className={'form-data-textarea-label'}>
                        Tags:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            When applicable, select a tag or multiple (command/select) tags to alert Heather that this
                            date has a notable entry.
                        </div>
                        <select
                            className={"form-data-textarea-input-select"}
                            name="tags"
                            value={formData.tags}
                            onChange={handleChange}
                            multiple
                        >
                            <option value="Chore">CHORE</option>
                            <option value="CAD">CAD</option>
                            <option value="Outing">OUTING</option>
                            <option value="Meal Planning and Grocery Shopping">MEAL PLAN and GROCERY SHOP</option>
                            <option value="New Skill">NEW SKILL</option>
                            <option value="Behavior Issue">BEHAVIOR ISSUE</option>
                            <option value="Fire Drill">FIRE DRILL</option>
                            <option value="Shower">SHOWER</option>
                        </select>
                    </div>
                </div>

                <div className={"form-data-date-block"}>
                    <label>
                        Date:
                    </label>
                    <input
                        type="date"
                        name="date"
                        value={formData.date}
                        onChange={handleChange}
                        required
                    />
                    <div className={"form-data-textarea-input-hint hint-block-bottom-border form-data-date-block-hint"}>
                        {dateMessage}
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-wake"}>
                    <label className={'form-data-textarea-label'}>
                        Wake:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <p className={"bold"}>Cheatsheet</p>
                            <ul>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Alex woke on her own around 730am and took herself to the bathroom.')}>Alex woke on her own around 730am and took herself to the bathroom.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Alex woke at 600am, went to bathroom, and went back to bed.')}>Alex woke at 600am, went to bathroom, and went back to bed.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Alex woke back up at 730am.')}>Alex woke back up at 730am.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Alex slept in until 830am.')}>Alex slept in until 830am.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Sponsor/Relief woke Alex at 730am.')}>Sponsor/Relief woke Alex at 730am.</li>
                            </ul>
                        </div>
                        <textarea
                            className={"form-data-textarea-input-textarea"}
                            name="wake"
                            value={formData.wake}
                            onChange={handleChange}
                            onDrop={(e) => handleDrop(e, 'wake')}
                            onDragOver={handleDragOver}
                        ></textarea>
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-morning"}>
                    <label className={'form-data-textarea-label'}>
                        Morning:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <p className={"bold"}>Cheatsheet</p>
                            <ul>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Sponsor/Relief helped Alex brush teeth and hair. ')}>Sponsor/Relief helped Alex brush teeth and hair.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Sponsor/Relief helped Alex get dressed and brush her teeth and hair. ')}>Sponsor/Relief helped Alex get dressed and brush her teeth and hair.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Sponsor/Relief helped Alex pick out an outfit from the ones hanging outside her dresser. ')}>Sponsor/Relief helped Alex pick out an outfit from the ones hanging outside her dresser.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Alex picked out an outfit from the ones hanging outside her dresser. ')}>Alex picked out an outfit from the ones hanging outside her dresser.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Alex picked a pink dress with a black tights and got dressed. ')}>Alex picked a pink dress with a black tights and got dressed.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Alex put her shoes on the wrong feet and Sponsor/Relief helped Alex put them on right. ')}>Alex put her shoes on the wrong feet and Sponsor/Relief helped Alex put them on right.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Alex put her dress on backwards and Sponsor/Relief helped Alex put it on right. ')}>Alex put her dress on backwards and Sponsor/Relief helped Alex put it on right.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Alex cleaned her room and organized her shelves. Sponsor/Relief checked on Alex until she completed her chores. ')}>Alex cleaned her room and organized her shelves. Sponsor/Relief checked on Alex until she completed her chores.</li>
                            </ul>
                        </div>
                        <textarea
                            className={"form-data-textarea-input-textarea"}
                            name="morning"
                            value={formData.morning}
                            onChange={handleChange}
                            onDrop={(e) => handleDrop(e, 'morning')}
                            onDragOver={handleDragOver}
                        ></textarea>
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-breakfast"}>
                    <label className={'form-data-textarea-label'}>
                        Breakfast:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <p className={"bold"}>Cheatsheet</p>
                            <ul>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Sponsor/Relief prepared cereal, monitored Alex as she ate and administered AM meds.')}>Sponsor/Relief prepared cereal, monitored Alex as she ate and administered AM meds.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Sponsor/Relief asked Alex if she wanted cereal or oats for breakfast. Alex picked Oats.')}>Sponsor/Relief asked Alex if she wanted cereal or oats for breakfast. Alex picked Oats.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Alex wanted cereal for breakfast.')}>Alex wanted cereal for breakfast.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Sponsor/Relief prepared breakfast.')}>Sponsor/Relief prepared breakfast.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Sponsor/Relief already prepared breakfast.')}>Sponsor/Relief already prepared breakfast.</li>
                                <li draggable onDragStart={(e) => handleDragStart(e, 'Sponsor/Relief monitored Alex as she ate and administered AM meds.')}>Sponsor/Relief monitored Alex as she ate and administered AM meds.</li>
                                <li>Sponsor/Relief prepared cereal, monitored Alex as she ate and administered AM
                                    meds.
                                </li>
                                <li>Sponsor/Relief asked Alex if she wanted cereal or oats for breakfast. Alex picked
                                    Oats.
                                </li>
                                <li>Alex wanted cereal for breakfast.</li>
                                <li>Sponsor/Relief prepared breakfast.</li>
                                <li>Sponsor/Relief already prepared breakfast.</li>
                                <li>Sponsor/Relief monitored Alex as she ate and administered AM meds.</li>
                                <li>Alex ate Breakfast and was monitored by Sponsor/Relief.</li>
                                <li>Sponsor/Relief administer AM meds.</li>
                                <li>While eating. Sponsor, Relief and Alex discussed the menu for the upcoming week and
                                    made a small shopping list.
                                </li>
                                <li>Alex helped clear the table.</li>
                                <li>Sponsor/Relief made Alex tea and made sure it was the right temperature.</li>
                                <li>Alex drank her tea until the van arrived.</li>
                                <li>Alex watched TV until the van arrived.</li>
                                <li>Van arrived at 830am to take Alex to Diversity.</li>
                                <li>Kiearra arrived at 800am to take Alex out for a girl's day.</li>
                            </ul>
                        </div>
                        <textarea
                            className={"form-data-textarea-input-textarea"}
                            name="breakfast"
                            value={formData.breakfast}
                            onChange={handleChange}
                        ></textarea>
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-midday"}>
                    <label className={'form-data-textarea-label'}>
                        Midday:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <p className={"bold"}>Cheatsheet</p>
                            <ul>
                                <li>Alex helped Sponsor put away some laundry.</li>
                                <li>Sponsor and Alex picked out outfits for the upcoming week at Diversity and hung them
                                    outside her dresser.
                                </li>
                                <li>Sponsor/Relief and Alex took Izzy for a quick neighborhood walk.</li>
                                <li>Sponsor/Relief asked Alex if she wanted to work on her new pop-together beads. Alex
                                    said Yes and worked on them in the Family room for a while.
                                </li>
                                <li>Sponsor/Relief ran a fire drill, helped Alex respond to the alarm and helped Alex
                                    evacuate to the meeting place in the front yard.
                                </li>
                                <li>Sponsor/Relief and Alex took Izzy for a quick neighborhood walk.</li>
                                <li>Sponsor/Relief and Alex took Izzy to Huguenot Park. Alex walked 1.85 miles.</li>
                                <li>Sponsor/Relief and Alex took Izzy to the water tower to walk a couple laps around
                                    the track.
                                </li>
                                <li>Sponsor/Relief and Alex took Izzy back home.</li>
                                <li>Sponsor/Relief and Alex went to Wegmans and picked up the groceries on the list.
                                    Alex helped push the cart, load the car and unload the car at home.
                                </li>
                            </ul>
                        </div>
                        <textarea
                            className={"form-data-textarea-input-textarea"}
                            name="midday"
                            value={formData.midday}
                            onChange={handleChange}
                        ></textarea>
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-lunch"}>
                    <label className={'form-data-textarea-label'}>
                        Lunch:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <p className={"bold"}>Cheatsheet</p>
                            <ul>
                                <li>Sponsor/Relief prepared lunch and monitored Alex as she ate.</li>
                                <li>Alex watched a little tv while Sponsor/Relief prepared lunch.</li>
                                <li>Alex helped Sponsor cook home-made granola.</li>
                                <li>Alex helped clear the table.</li>
                            </ul>
                        </div>
                        <textarea
                            className={"form-data-textarea-input-textarea"}
                            name="lunch"
                            value={formData.lunch}
                            onChange={handleChange}
                        ></textarea>
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-afternoon"}>
                    <label className={'form-data-textarea-label'}>
                        Afternoon:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <p className={"bold"}>Cheatsheet</p>
                            <ul>
                                <li>Van returned at 230pm.</li>
                                <li>Kiearra returned at 330pm.</li>
                                <li>Alex helped Sponsor put away some laundry.</li>
                                <li>Sponsor and Alex picked out outfits for the upcoming week at Diversity and hung them
                                    outside her dresser.
                                </li>
                                <li>Sponsor/Relief and Alex took Izzy for a quick neighborhood walk</li>
                                <li>Sponsor/Relief asked Alex if she wanted to work on her new pop-together beads. Alex
                                    said Yes and worked on them in the Family room for a while.
                                </li>
                                <li>Sponsor/Relief ran a fire drill, helped Alex respond to the alarm and helped Alex
                                    evacuate to the meeting place in the front yard.
                                </li>
                                <li>Sponsor/Relief and Alex took Izzy for a quick neighborhood walk.</li>
                                <li>Sponsor/Relief and Alex took Izzy to Huguenot Park. Alex walked 1.85 miles.</li>
                                <li>Sponsor/Relief and Alex took Izzy to the water tower to walk a couple laps around
                                    the track.
                                </li>
                                <li>Sponsor/Relief and Alex took Izzy back home.</li>
                                <li>Sponsor/Relief and Alex went to Wegmans and picked up the groceries on the list.
                                    Alex helped push the cart, load the car and unload the car at home.
                                </li>
                            </ul>
                        </div>
                        <textarea
                            className={"form-data-textarea-input-textarea"}
                            name="afternoon"
                            value={formData.afternoon}
                            onChange={handleChange}
                        ></textarea>
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-dinner"}>
                    <label className={'form-data-textarea-label'}>
                        Dinner:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <p className={"bold"}>Cheatsheet</p>
                            <ul>
                                <li>Sponsor/Relief prepared dinner, monitored Alex as she ate and administered PM
                                    meds.
                                </li>
                                <li>Sponsor/Relief prepared dinner while Alex watched TV, monitored Alex as she ate and
                                    administered PM Meds.
                                </li>
                                <li>Sponsor/Relief prepared dinner and monitored Alex as she ate.</li>
                                <li>Sponsor/Relief administered PM meds.</li>
                                <li>Alex helped clear the table.</li>
                            </ul>
                        </div>
                        <textarea
                            className={"form-data-textarea-input-textarea"}
                            name="dinner"
                            value={formData.dinner}
                            onChange={handleChange}
                        ></textarea>
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-evening"}>
                    <label className={'form-data-textarea-label'}>
                        Evening:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <p className={"bold"}>Cheatsheet</p>
                            <ul>
                                <li>Sponsor/Relief and Alex took Izzy for a quick neighborhood walk.</li>
                                <li>Sponsor/Relief and Alex took Izzy to Huguenot Park. Alex walked 1.85 miles.</li>
                                <li>Sponsor/Relief and Alex took Izzy to the water tower to walk a couple laps around
                                    the track.
                                </li>
                                <li>Sponsor/Relief went to Target.</li>
                                <li>Relief and Alex waited in the car while Sponsor shopped.</li>
                                <li>Alex helped push the car, load the car and unload the car back home.</li>
                                <li>Sponsor/Relief asked Alex if she wanted to work on a beading craft. Alex said yes.
                                    Sponsor set Alex up with beads in the family room and checked in on her as she
                                    worked on it.
                                </li>
                                <li>Relief and Alex worked on making pop-together bracelets while watching TV.</li>
                                <li>Sponsor, Relief and Alex watched a movie on TV.</li>
                            </ul>
                        </div>
                        <textarea
                            className={"form-data-textarea-input-textarea"}
                            name="evening"
                            value={formData.evening}
                            onChange={handleChange}
                        ></textarea>
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-bed"}>
                    <label className={'form-data-textarea-label'}>
                        Bed:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <p className={"bold"}>Cheatsheet</p>
                            <ul>
                                <li>Sponsor/Relief helped Alex shower and with ADLs and helped Alex put on PJs.</li>
                                <li>Alex watched TV with family until she was tired and went to bed around 730pm.</li>
                                <li>Sponsor/Relief helped Alex brush teeth and hair and put on PJs.</li>
                                <li>Alex watched TV with family until she was tired and went to bed around 730pm.</li>
                                <li>Alex went to bed around 730pm.</li>
                            </ul>
                        </div>
                        <textarea
                            className={"form-data-textarea-input-textarea"}
                            name="bed"
                            value={formData.bed}
                            onChange={handleChange}
                        ></textarea>
                    </div>
                </div>

                <div className={"form-data-submit-block"}>
                    <div className={"form-data-submit_button-block"}>
                        <button className={"form-data-submit-button"} type="submit">Submit</button>
                    </div>
                    <div className={"form-data-submit_message-block"}>
                        {message && <p className={"alert"}>{message}</p>}
                    </div>
                </div>
            </form>

            <div className={"form-data-results"}>
                {logEntry && <pre>{logEntry}</pre>}
            </div>
        </div>
    );
}

export default ActivityForm;
