import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ActivityForm() {
    const [formData, setFormData] = useState({ date: '', activity: '' });
    const [message, setMessage] = useState('');
    const [dateMessage, setDateMessage] = useState('');

    useEffect(() => {
        setDateMessage('Watch here for hints.');
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
        if (name === 'date') {
            checkDay(value);
        }
    };

    const checkDay = (date) => {
        const selectedDate = new Date(date + 'T00:00:00');
        if (selectedDate.getDay() === 0) {
            setDateMessage('This is a Sunday. Add a "Meal Planning and Grocery Shopping" line item for today.');
        } else if (selectedDate.getDay() === 6) {
            setDateMessage('This is a Saturday. Add a "Outing" line item for today.');
        } else {
            setDateMessage('Watch here for hints.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5001/save-log', formData);
            setMessage(response.data.message);
        } catch (error) {
            if (error.response && error.response.status === 400) {
                setMessage('Date has already been submitted');
            } else {
                setMessage('Error saving activity log');
            }
            console.error('Error saving activity log:', error);
        }
    };

    return (
        <div>
            <h1>Alex Activity Log</h1>
            {message && <p className={"alert"}>{message}</p>}
            <div className={"form-data-hint-general hint-block"}>
                <ul>
                    <li>Add "Sponsor/Relief helped Alex shower and with her ADLs" at least 3 times a week.</li>
                    <li>Add "Sponsor and Relief conducted a Fire Drill and helped Alex leave the house when the alarm went off and helped Alex walk to the meeting point in the front yard." at least 1 times a month.</li>
                    <li>Add "Fire Drill" at least 1 times a month.</li>
                </ul>
            </div>

            <form onSubmit={handleSubmit}>
                <div className={"form-data-textarea-block form-data-container-tags"}>
                    <label className={'form-data-textarea-label'}>
                        Tags:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <p>When applicable, add a tag or multiple tags to notify Heather that this date has a notable entry.</p>
                            <ul>
                                <li>Shower</li>
                                <li>Fire Drill</li>
                                <li>Behavior Issue</li>
                                <li>CAD</li>
                                <li>New Skill</li>
                                <li>Outing</li>
                            </ul>
                        </div>
                        <input
                            className={"form-data-textarea-input-input"}
                            name="tags"
                            value={formData.tags}
                            onChange={handleChange}
                        />
                    </div>
                </div>

                <div className={"form-data-date-block"}>
                    <label>
                        Date:
                    </label>
                    <input
                        type="date"
                        name="date"
                        value={formData.date}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className={"form-data-textarea-block form-data-container-morning"}>
                    <label className={'form-data-textarea-label'}>
                        Morning:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            {dateMessage}
                        </div>
                        <textarea
                            className={"form-data-textarea-input-textarea"}
                            name="morning"
                            value={formData.morning}
                            onChange={handleChange}
                            required
                        />
                    </div>
                </div>

                <button className={"form-data-submit-button"} type="submit">Submit</button>
            </form>
        </div>
    );
}

export default ActivityForm;
