import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Function to format a date as 3/20/2024 - THURSDAY
const formatDate = (date) => {
    const dayOfWeek = new Intl.DateTimeFormat("en-US", { weekday: "long" }).format(date).toUpperCase();
    const formattedDate = `${date.getMonth() + 1}/${date.getDate()}/${date.getFullYear()} - ${dayOfWeek}`;
    return formattedDate;
};

function ActivityForm() {
    const [formData, setFormData] = useState({ date: '', activity: '', tags: [], morning: [], wake: '' });
    const [message, setMessage] = useState('');
    const [dateMessage, setDateMessage] = useState('');
    const [logEntry, setLogEntry] = useState('');

    useEffect(() => {
        setDateMessage('Watch here for hints.');
    }, []);

    const handleChange = (e) => {
        const { name, value, options } = e.target;
        if ((name === 'tags' || name === 'morning' || name === 'midday') && options) {
            const selectedTags = Array.from(options).filter(option => option.selected).map(option => option.value);
            setFormData({ ...formData, [name]: selectedTags });
        } else {
            setFormData({ ...formData, [name]: value });
            if (name === 'date') {
                checkDay(value);
            }
        }
    };

    const checkDay = (date) => {
        const selectedDate = new Date(date + 'T00:00:00');
        if (selectedDate.getDay() === 0) {
            setDateMessage('This is a Sunday. Add a "Meal Planning and Grocery Shopping" and a "Shower" line item for today.');
        } else if (selectedDate.getDay() === 6) {
            setDateMessage('This is a Saturday. Add an "Outing" line item for today.');
        } else {
            setDateMessage('Watch here for hints.');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5001/save-log', formData);
            setMessage(response.data.message);

            const formattedDate = formatDate(new Date(formData.date + 'T00:00:00'));
            const logEntry = `\r\n\r\n${formData.tags.length > 0 ? `NOTE:${formData.tags.join('. ')}. \r\n` : ''}${formattedDate}\r\n${formData.wake} ${formData.morning} ${formData.breakfast} ${formData.midday} `;
            setLogEntry(logEntry);
        } catch (error) {
            if (error.response && error.response.status === 400) {
                setMessage('Date has already been submitted');
            } else {
                setMessage('Error saving activity log');
            }
            console.error('Error saving activity log:', error);
        }
    };

    return (
        <div>
            <h1>Alex Activity Log</h1>

            <div className={"form-data-hint-general hint-block-bottom-border"}>
                <ul>
                    <li>Add "Sponsor/Relief helped Alex shower and with her ADLs" at least 3 times a week.</li>
                    <li>Add "Sponsor and Relief conducted a Fire Drill and helped Alex leave the house when the alarm went off and helped Alex walk to the meeting point in the front yard." at least 1 times a month.</li>
                    <li>Add "Fire Drill" at least 1 times a month.</li>
                </ul>
            </div>

            <form onSubmit={handleSubmit}>
                <div className={"form-data-textarea-block form-data-container-tags"}>
                    <label className={'form-data-textarea-label'}>
                        Tags:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            When applicable, select a tag or multiple (command/select) tags to alert Heather that this
                            date has a notable entry.
                        </div>
                        <select
                            className={"form-data-textarea-input-select"}
                            name="tags"
                            value={formData.tags}
                            onChange={handleChange}
                            multiple
                        >
                            <option value="Chore">Chore</option>
                            <option value="CAD">CAD</option>
                            <option value="New Skill">New Skill</option>
                            <option value="Outing">Outing</option>
                            <option value="Fire Drill">Fire Drill</option>
                            <option value="Behavior Issue">Behavior Issue</option>
                            <option value="Shower">Shower</option>
                        </select>
                    </div>
                </div>

                <div className={"form-data-date-block"}>
                    <label>
                        Date:
                    </label>
                    <input
                        type="date"
                        name="date"
                        value={formData.date}
                        onChange={handleChange}
                        required
                    />
                    <div className={"form-data-textarea-input-hint hint-block-bottom-border form-data-date-block-hint"}>
                        {dateMessage}
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-wake"}>
                    <label className={'form-data-textarea-label'}>
                        Wake:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <ul>
                                <li>Alex woke on her own around 730am and took herself to the bathroom.</li>
                                <li>Alex woke at 600am, went to bathroom, and went back to bed.</li>
                                <li>Alex woke back up at 730am.</li>
                                <li>Alex slept in until 830am.</li>
                                <li>Sponsor/Relief woke Alex at 730am.</li>
                            </ul>
                        </div>
                        <input
                            className={"form-data-textarea-input-input"}
                            name="wake"
                            value={formData.wake}
                            onChange={handleChange}
                        ></input>
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-morning"}>
                    <label className={'form-data-textarea-label'}>
                        Morning:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            Select one or multiple (command/select) morning items.
                        </div>
                        <select
                            className={"form-data-textarea-input-select"}
                            name="morning"
                            value={formData.morning}
                            onChange={handleChange}
                            multiple
                        >
                            <option value="Sponsor/Relief helped Alex brush teeth and hair.">Sponsor/Relief helped Alex
                                brush teeth and hair.
                            </option>
                            <option
                                value="Sponsor/Relief helped Alex get dressed and brush her teeth and hair.">Sponsor/Relief
                                helped Alex get dressed and brush her teeth and hair.
                            </option>
                            <option
                                value="Sponsor/Relief helped Alex pick out an outfit from the ones hanging outside her dresser.">Sponsor/Relief
                                helped Alex pick out an outfit from the ones hanging outside her dresser.
                            </option>
                            <option value="Alex picked out an outfit from the ones hanging outside her dresser.">Alex
                                picked out an outfit from the ones hanging outside her dresser.
                            </option>
                            <option value="Alex picked a pink dress with a black tights and got dressed.">Alex picked a
                                pink dress with a black tights and got dressed.
                            </option>
                            <option
                                value="Alex put her shoes on the wrong feet and Sponsor/Relief helped Alex put them on right.">Alex
                                put her shoes on the wrong feet and Sponsor/Relief helped Alex put them on right.
                            </option>
                            <option
                                value="Alex put her dress on backwards and Sponsor/Relief helped Alex put it on right.">Alex
                                put her dress on backwards and Sponsor/Relief helped Alex put it on right.
                            </option>
                            <option
                                value="Alex cleaned her room and organized her shelves. Sponsor/Relief checked on Alex until she completed her chores.">Alex
                                cleaned her room and organized her shelves. Sponsor/Relief checked on Alex until she
                                completed her chores.
                            </option>
                        </select>
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-breakfast"}>
                    <label className={'form-data-textarea-label'}>
                        Breakfast:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <ul>
                                <li>Sponsor/Relief prepared cereal, monitored Alex as she ate and administered AM
                                    meds.
                                </li>
                                <li>Sponsor/Relief asked Alex if she wanted cereal or oats for breakfast. Alex picked
                                    Oats.
                                </li>
                                <li>Alex wanted cereal for breakfast.</li>
                                <li>Sponsor/Relief prepared breakfast.</li>
                                <li>Sponsor/Relief already prepared breakfast.</li>
                                <li>Sponsor/Relief monitored Alex as she ate and administered AM meds.</li>
                                <li>Alex ate Breakfast and was monitored by Sponsor/Relief.</li>
                                <li>Sponsor/Relief administer AM meds.</li>
                                <li>While eating. Sponsor, Relief and Alex discussed the menu for the upcoming week and
                                    made a small shopping list.
                                </li>
                                <li>Alex helped clear the table.</li>
                                <li>Sponsor/Relief made Alex tea and made sure it was the right temperature.</li>
                                <li>Alex drank her tea until the van arrived.</li>
                                <li>Alex watched TV until the van arrived.</li>
                                <li>Van arrived at 830am to take Alex to Diversity.</li>
                                <li>Kiearra arrived at 800am to take Alex out for a girl's day.</li>
                            </ul>
                        </div>
                        <input
                            className={"form-data-textarea-input-input"}
                            name="breakfast"
                            value={formData.breakfast}
                            onChange={handleChange}
                        ></input>
                    </div>
                </div>

                <div className={"form-data-textarea-block form-data-container-midday"}>
                    <label className={'form-data-textarea-label'}>
                        Midday:
                    </label>
                    <div className={'form-data-textarea-input'}>
                        <div className={"form-data-textarea-input-hint hint-block"}>
                            <ul>
                                <li>Alex helped Sponsor put away some laundry.</li>
                                <li>Sponsor and Alex picked out outfits for the upcoming week at Diversity and hung them outside her dresser.</li>
                                <li>Sponsor/Relief and Alex took Izzy for a quick neighborhood walk.</li>
                                <li>Sponsor/Relief asked Alex if she wanted to work on her new pop-together beads. Alex said Yes and worked on them in the Family room for a while.</li>
                                <li>Sponsor/Relief ran a fire drill, helped Alex respond to the alarm and helped Alex evacuate to the meeting place in the front yard.</li>
                                <li>Sponsor/Relief and Alex took Izzy for a quick neighborhood walk.</li>
                                <li>Sponsor/Relief and Alex took Izzy to Huguenot Park. Alex walked 1.85 miles.</li>
                                <li>Sponsor/Relief and Alex took Izzy to the water tower to walk a couple laps around the track.</li>
                                <li>Sponsor/Relief and Alex took Izzy back home.</li>
                                <li>Sponsor/Relief and Alex went to Wegmans and picked up the groceries on the list. Alex helped push the cart, load the car and unload the car at home.</li>
                            </ul>
                        </div>
                        <textarea
                            className={"form-data-textarea-input-textarea"}
                            name="midday"
                            value={formData.midday}
                            onChange={handleChange}
                        ></textarea>
                    </div>
                </div>


                <div className={"form-data-submit-block"}>
                    <div className={"form-data-submit_button-block"}>
                        <button className={"form-data-submit-button"} type="submit">Submit</button>
                    </div>
                    <div className={"form-data-submit_message-block"}>
                        {message && <p className={"alert"}>{message}</p>}
                    </div>
                </div>
            </form>

            <div className={"form-data-results"}>
                {logEntry && <pre>{logEntry}</pre>}
            </div>
        </div>
    );
}

export default ActivityForm;
